umber:1,columnNumber:85},this)},void 0,!1,{fileName:"/Users/saifnasir/Desktop/magicui/content/docs/components/_mdx_bundler_entry_point-836f99a8-c1aa-4b00-adf4-b64441fdfb7e.mdx",lineNumber:1,columnNumber:79},this)},void 0,!1,{fileName:"/Users/saifnasir/Desktop/magicui/content/docs/components/_mdx_bundler_entry_point-836f99a8-c1aa-4b00-adf4-b64441fdfb7e.mdx",lineNumber:1,columnNumber:1},this)},void 0,!1,{fileName:"/Users/saifnasir/Desktop/magicui/content/docs/components/_mdx_bundler_entry_point-836f99a8-c1aa-4b00-adf4-b64441fdfb7e.mdx",lineNumber:21,columnNumber:1},this)},void 0,!1,{fileName:"/Users/saifnasir/Desktop/magicui/content/docs/components/_mdx_bundler_entry_point-836f99a8-c1aa-4b00-adf4-b64441fdfb7e.mdx",lineNumber:19,columnNumber:1},this),(0,n.jsxDEV)(F,{value:"manual",children:(0,n.jsxDEV)(A,{children:[(0,n.jsxDEV)(E,{children:"Copy and paste the following code into your project."},void 0,!1,{fileName:"/Users/saifnasir/Desktop/magicui/content/docs/components/_mdx_bundler_entry_point-836f99a8-c1aa-4b00-adf4-b64441fdfb7e.mdx",lineNumber:31,columnNumber:1},this),(0,n.jsxDEV)(v,{name:"animated-list",children:(0,n.jsxDEV)(e.div,{"data-rehype-pretty-code-fragment":"",children:(0,n.jsxDEV)(e.pre,{"data-language":"tsx","data-theme":"default",__rawString__:`"use client";

import React, { ReactElement, useEffect, useMemo, useState } from "react";
import { AnimatePresence, motion } from "framer-motion";

export interface AnimatedListProps {
  className?: string;
  children: React.ReactNode;
  delay?: number;
}

export const AnimatedList = React.memo(
  ({ className, children, delay = 1000 }: AnimatedListProps) => {
    const [index, setIndex] = useState(0);
    const childrenArray = React.Children.toArray(children);

    useEffect(() => {
      const interval = setInterval(() => {
        setIndex((prevIndex) => (prevIndex + 1) % childrenArray.length);
      }, delay);

      return () => clearInterval(interval);
    }, [childrenArray.length, delay]);

    const itemsToShow = useMemo(
      () => childrenArray.slice(0, index + 1).reverse(),
      [index, childrenArray],
    );

    return (
      <div className={\`flex flex-col items-center gap-4 \${className}\`}>
        <AnimatePresence>
          {itemsToShow.map((item) => (
            <AnimatedListItem key={(item as ReactElement).key}>
              {item}
            </AnimatedListItem>
          ))}
        </AnimatePresence>
      </div>
    );
  },
);

AnimatedList.displayName = "AnimatedList";

export function AnimatedListItem({ children }: { children: React.ReactNode }) {
  const animations = {
    initial: { scale: 0, opacity: 0 },
    animate: { scale: 1, opacity: 1, originY: 0 },
    exit: { scale: 0, opacity: 0 },
    transition: { type: "spring", stiffness: 350, damping: 40 },
  };

  return (
    <motion.div {...animations} layout className="mx-auto w-full">
      {children}
    </motion.div>
  );
}
