r:1,columnNumber:85},this)},void 0,!1,{fileName:"/Users/saifnasir/Desktop/magicui/content/docs/components/_mdx_bundler_entry_point-4a5fe3e4-ed21-47fa-9cd2-9e27a8d231eb.mdx",lineNumber:1,columnNumber:79},this)},void 0,!1,{fileName:"/Users/saifnasir/Desktop/magicui/content/docs/components/_mdx_bundler_entry_point-4a5fe3e4-ed21-47fa-9cd2-9e27a8d231eb.mdx",lineNumber:1,columnNumber:1},this)},void 0,!1,{fileName:"/Users/saifnasir/Desktop/magicui/content/docs/components/_mdx_bundler_entry_point-4a5fe3e4-ed21-47fa-9cd2-9e27a8d231eb.mdx",lineNumber:21,columnNumber:1},this)},void 0,!1,{fileName:"/Users/saifnasir/Desktop/magicui/content/docs/components/_mdx_bundler_entry_point-4a5fe3e4-ed21-47fa-9cd2-9e27a8d231eb.mdx",lineNumber:19,columnNumber:1},this),(0,n.jsxDEV)(F,{value:"manual",children:(0,n.jsxDEV)(C,{children:[(0,n.jsxDEV)(E,{children:"Copy and paste the following code into your project."},void 0,!1,{fileName:"/Users/saifnasir/Desktop/magicui/content/docs/components/_mdx_bundler_entry_point-4a5fe3e4-ed21-47fa-9cd2-9e27a8d231eb.mdx",lineNumber:31,columnNumber:1},this),(0,n.jsxDEV)(v,{name:"fade-text",children:(0,n.jsxDEV)(e.div,{"data-rehype-pretty-code-fragment":"",children:(0,n.jsxDEV)(e.pre,{"data-language":"tsx","data-theme":"default",__rawString__:`"use client";

import { useMemo } from "react";
import { motion, Variants } from "framer-motion";

type FadeTextProps = {
  className?: string;
  direction?: "up" | "down" | "left" | "right";
  framerProps?: Variants;
  text: string;
};

export function FadeText({
  direction = "up",
  className,
  framerProps = {
    hidden: { opacity: 0 },
    show: { opacity: 1, transition: { type: "spring" } },
  },
  text,
}: FadeTextProps) {
  const directionOffset = useMemo(() => {
    const map = { up: 10, down: -10, left: -10, right: 10 };
    return map[direction];
  }, [direction]);

  const axis = direction === "up" || direction === "down" ? "y" : "x";

  const FADE_ANIMATION_VARIANTS = useMemo(() => {
    const { hidden, show, ...rest } = framerProps as {
      [name: string]: { [name: string]: number; opacity: number };
    };

    return {
      ...rest,
      hidden: {
        ...(hidden ?? {}),
        opacity: hidden?.opacity ?? 0,
        [axis]: hidden?.[axis] ?? directionOffset,
      },
      show: {
        ...(show ?? {}),
        opacity: show?.opacity ?? 1,
        [axis]: show?.[axis] ?? 0,
      },
    };
  }, [directionOffset, axis, framerProps]);

  return (
    <motion.div
      initial="hidden"
      animate="show"
      viewport={{ once: true }}
      variants={FADE_ANIMATION_VARIANTS}
    >
      <motion.span className={className}>{text}</motion.span>
    </motion.div>
  );
}
