umber:1,columnNumber:85},this)},void 0,!1,{fileName:"/Users/saifnasir/Desktop/magicui/content/docs/components/_mdx_bundler_entry_point-159c4f87-5e4d-4b58-b8cb-71f3c7030fd2.mdx",lineNumber:1,columnNumber:79},this)},void 0,!1,{fileName:"/Users/saifnasir/Desktop/magicui/content/docs/components/_mdx_bundler_entry_point-159c4f87-5e4d-4b58-b8cb-71f3c7030fd2.mdx",lineNumber:1,columnNumber:1},this)},void 0,!1,{fileName:"/Users/saifnasir/Desktop/magicui/content/docs/components/_mdx_bundler_entry_point-159c4f87-5e4d-4b58-b8cb-71f3c7030fd2.mdx",lineNumber:21,columnNumber:1},this)},void 0,!1,{fileName:"/Users/saifnasir/Desktop/magicui/content/docs/components/_mdx_bundler_entry_point-159c4f87-5e4d-4b58-b8cb-71f3c7030fd2.mdx",lineNumber:19,columnNumber:1},this),(0,n.jsxDEV)(v,{value:"manual",children:(0,n.jsxDEV)(P,{children:[(0,n.jsxDEV)(g,{children:"Copy and paste the following code into your project."},void 0,!1,{fileName:"/Users/saifnasir/Desktop/magicui/content/docs/components/_mdx_bundler_entry_point-159c4f87-5e4d-4b58-b8cb-71f3c7030fd2.mdx",lineNumber:31,columnNumber:1},this),(0,n.jsxDEV)(F,{name:"letter-pullup",children:(0,n.jsxDEV)(e.div,{"data-rehype-pretty-code-fragment":"",children:(0,n.jsxDEV)(e.pre,{"data-language":"tsx","data-theme":"default",__rawString__:`"use client";

import { motion } from "framer-motion";

import { cn } from "@/lib/utils";

interface LetterPullupProps {
  className?: string;
  words: string;
  delay?: number;
}

export default function LetterPullup({
  className,
  words,
  delay,
}: LetterPullupProps) {
  const letters = words.split("");

  const pullupVariant = {
    initial: { y: 100, opacity: 0 },
    animate: (i: any) => ({
      y: 0,
      opacity: 1,
      transition: {
        delay: i * (delay ? delay : 0.05), // By default, delay each letter's animation by 0.05 seconds
      },
    }),
  };

  return (
    <div className="flex justify-center">
      {letters.map((letter, i) => (
        <motion.h1
          key={i}
          variants={pullupVariant}
          initial="initial"
          animate="animate"
          custom={i}
          className={cn(
            "font-display text-center text-4xl font-bold tracking-[-0.02em] text-black drop-shadow-sm dark:text-white md:text-4xl md:leading-[5rem]",
            className,
          )}
        >
          {letter === " " ? <span>&nbsp;</span> : letter}
        </motion.h1>
      ))}
    </div>
  );
}
